    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
   
    float celsius, fahrenheit;
    if(jTextField1.getText().isEmpty() && !jTextField2.getText().isEmpty()){
        fahrenheit = (float)((Double.parseDouble(jTextField2.getText() ) - 32) / 1.800);
        jLabel3.setText(fahrenheit + "째 Celsius");
    }
    else if(!jTextField1.getText().isEmpty() && jTextField2.getText().isEmpty()){
        celsius =(float)(Double.parseDouble(jTextField1.getText())*1.8 + 32);
        jLabel2.setText(celsius + "째 Fahrenheit"); 
    }
    else if(!jTextField1.getText().isEmpty() && !jTextField2.getText().isEmpty()){
        celsius =(float)(Double.parseDouble(jTextField1.getText())*1.8 + 32);
        jLabel2.setText(celsius + "째 Fahrenheit"); 
        fahrenheit = (float)((Double.parseDouble(jTextField2.getText() ) - 32) / 1.800);
        jLabel3.setText(fahrenheit + "째 Celsius");
    
    }
    }//GEN-LAST:event_jButton1ActionPerformed

