    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
    float a,b,c,P,A;
    a = (float)Double.parseDouble(jTextField1.getText());
    b = (float)Double.parseDouble(jTextField2.getText());
    c = (float)Double.parseDouble(jTextField3.getText());
    P = (a + b + c) / 2;
    A = (float)Math.sqrt(P * (P - a) * (P - b) * (P - c));
    jLabel9.setText("Aria = " + A);
    }//GEN-LAST:event_jButton1ActionPerformed

