    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
      //Diametru
        float temp;
      temp = (float)Double.parseDouble(jTextField5.getText()) * 2;
        jLabel10.setText("Diametru = " + temp);
      //perimetru  
      temp = (float)(Double.parseDouble(jTextField5.getText()) * 3.14); 
       jLabel11.setText("Perimetru = " + temp);
       //aria
       
       temp = (float)Double.parseDouble(jTextField5.getText());
       temp = temp * temp;
       temp = (float)(temp * 3.14);
       jLabel12.setText(" Aria = " + temp);
       
    }//GEN-LAST:event_jButton2ActionPerformed

