    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
         float temp =(float) Double.parseDouble(jTextField4.getText());
         float diagonala =(float) (temp * Math.sqrt(2));
         jLabel6.setText("Diagonala = " + diagonala);// Diagonala
         
         jLabel7.setText("Perimetrul = " + 4 * temp);//Perimetru
         jLabel8.setText("Aria = " + temp * temp);//Arie
    }//GEN-LAST:event_jButton4ActionPerformed

